dw:transform-message.dw:set-payload:
%dw 1.0
%output application/xml
---
{
	BackgroundReports: {
		ProviderReferenceId: {
			IdValue: payload.reportId as :string
		},
		BackgroundReportPackage @(type: "report"): {
			ClientReferenceId: {
				IdValue: payload.refId
			},
			SupportingDocumentation: {
				Documentation @(resulttype: "report"): {
					InternetWebAddress: payload.reportUrl
				}
			},
			ScreeningStatus: {
				OrderStatus: payload.status,
				DateOrderReceived: payload.orderDate
			}
		}
	}
}
